    text: str | None = None
    command: str | None = None
    output: str | None = None
class Agent:
    def __init__(self, workspace: Workspace):
        self.workspace = workspace
        self.llm = LLMClient()
    async def handle_user_message(self, text: str) -> AsyncIterator[AgentStep]:

class Workspace:
    def __init__(self, root: Path):
        self.root = root
    def read_file(self, relative: str) -> str:
        path = self.root / relative
        return path.read_text(encoding="utf-8")
    def write_file(self, relative: str, content: str) -> None:
        path = self.root / relative
        path.write_text(content, encoding="utf-8")

class LLMClient:
    def __init__(self):
        openai.api_key = API_KEY
    async def chat(self, prompt: str) -> str:
        response = openai.ChatCompletion.create(
            model=MODEL_NAME,
            messages=[{'role': 'user', 'content': prompt}]
        )
        return response.choices[0].message['content']

class AICoderApp(App):
    CSS_PATH = "aicoder.css"
    BINDINGS = [
        ("ctrl+c", "quit", "Quit"),
        ("f5", "run_agent", "Run agent on last prompt"),
    ]
    def __init__(self, project_path: str | None = None, **kwargs):
        super().__init__(**kwargs)
        self.workspace = Workspace(Path(project_path or ".").resolve())
        self.agent = Agent(self.workspace)
